/**
 * Pokédex Data - Solo formas base con info de evoluciones
 * Optimizado para rendimiento - ~300 líneas evolutivas
 */

export interface Evolution {
  id: number;
  name: string;
  nameEs: string;
  method: string;
}

export interface PokedexPokemon {
  id: number;
  pokemonId?: number; // Added for mapped data
  name: string;
  nameEs: string;
  gen: number;
  generation?: number; // Added for mapped data
  types: string[];
  stats: any; // Allow either [number, ...] or the object format
  desc: string;
  description?: string; // Added for mapped data
  h: number; // height
  w: number; // weight
  height?: number; // Added for mapped data
  weight?: number; // Added for mapped data
  evos?: Evolution[];
  sprites?: {
    sprite: string;
    spriteAnimated: string;
    artwork: string;
    cry: string;
  };
}

// Sprite URLs generator
export function getSprites(id: number) {
  const animated = id <= 649;
  return {
    sprite: `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id}.png`,
    spriteAnimated: animated
      ? `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-v/black-white/animated/${id}.gif`
      : `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/showdown/${id}.gif`,
    artwork: `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/${id}.png`,
    cry: `https://raw.githubusercontent.com/PokeAPI/cries/main/cries/pokemon/latest/${id}.ogg`,
  };
}

